    private void inditasButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_inditasButtonActionPerformed
        a.setBeinditva(true);
        if (a.isUzemanyag()) {
            if(defektCheckBox.isSelected()){
                a.megyEselyDefektre();
                if(a.isDefekt()){
                    allapotTextArea.setText("Defektet kaptal!");
                }
            }
            a.megy();
            allapotTextArea.setText("Az auto beindult, es megerkezett, viszont mar nincs uzemanyag!");

        }
        
    }//GEN-LAST:event_inditasButtonActionPerformed

    private void tankolButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tankolButtonActionPerformed
        if (!a.isBeinditva()) {
            a.tankol();
            allapotTextArea.setText("Tankolas...");
        } else {
            allapotTextArea.setText("Az auto nincsen leallitva, allitsd le mielott, tankolni szeretnel!");
        }
    }//GEN-LAST:event_tankolButtonActionPerformed

    private void leallitasButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_leallitasButtonActionPerformed
        a.setBeinditva(false);
        allapotTextArea.setText("Az auto leallt!");
    }//GEN-LAST:event_leallitasButtonActionPerformed

    private void defektCheckBoxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_defektCheckBoxActionPerformed
        if(defektCheckBox.isSelected() && !potkerekTextField.equals("")){
            String potkereStringCastElott = potkerekTextField.getText();
            int potkerekIntegerCast = Integer.parseInt(potkereStringCastElott);
            a =  new Auto(true,false, potkerekIntegerCast);
            
        }
    }//GEN-LAST:event_defektCheckBoxActionPerformed

