    private void kerekcsereButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kerekcsereButtonActionPerformed
        if(a.isDefekt()){
            a.kerekCsere();
            allapotTextArea.setText("Kerek kicserelve! 1 kerek kidobva.");
            int potkerekVisszaCastolasElott = a.getPotkerek();
            String potkerekStringCast = String.valueOf(potkerekVisszaCastolasElott);
            potkerekTextField.setText(potkerekStringCast);
        } 
        else {
            allapotTextArea.setText("Nem kaptal meg defektet!");
        }
             
        
       
    }//GEN-LAST:event_kerekcsereButtonActionPerformed

